INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (1, 'Vanilla', 'Vulnerable Candle', 3.99, 50, '/assets/products/vanilla/v1.png,/assets/products/vanilla/v2.png,/assets/products/vanilla/v3.png', 'This vanilla candle might be basic in look, smell, and boring in general, but with our home blend of essential oils, this vanilla is anything but boring! Sweetened with beeswax, this candle is fragrant without being overpowering. It is a nice scent that is fragrant, but not overpowering. It burns longer than most candles. If you have been searching for a nice fragrant candle that you can burn without burning the whole house down, then this candle is wonderful for you, and keeps the room smelling nice all day.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (2, 'Rose', 'Vulnerable Candle', 9.99, 25, '/assets/products/rose/v1.png,/assets/products/rose/v2.png,/assets/products/rose/v3.png', 'This Rose candle is what you need for a wonderfully calming and soothing experience. Our soy-based, hand-poured candles offer a captivating floral bouquet and a fragrant aroma that will set a tranquil atmosphere in any room. With a burn time of 3 to 4 hours, it\'s perfect to bring that special moment into your life.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (3, 'Sewage', 'Vulnerable Candle', 12.99, 12, '/assets/products/sewage/v1.png,/assets/products/sewage/v2.png,/assets/products/sewage/v3.png', 'Introducing our Sewage Candle - an essential addition to your space! With a unique and classic design, it emits a captivating scent of sewage that is gentle and soothing, great for calming and relaxing both the body and mind. Enjoy the elegant fragrances of still water, sewer rats, and garbage with one light of this candle!');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (4, 'Lavender', 'Vulnerable Candle', 8.99, 60, '/assets/products/lavender/v1.png,/assets/products/lavender/v2.png,/assets/products/lavender/v3.png', 'Our lavender candle helps you take a break from life’s stresses. Its irresistibly soothing scent provides a peaceful and calming atmosphere that invites deep relaxation into your living spaces. Let it invite you into a peaceful, calm state of mind.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (5, 'Coconut', 'Vulnerable Candle', 6.99, 18, '/assets/products/coconut/v1.png,/assets/products/coconut/v2.png,/assets/products/coconut/v3.png', 'Bring nature\'s beauty into your home with our coconut candle! Hand-crafted from vegan and plant-based ingredients, this candle is sure to create a calming atmosphere. Enjoy the aroma of coconut, and drift away in the luxurious scent of a tropical paradise.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (6, 'Chocolate', 'Vulnerable Candle', 9.99, 24, '/assets/products/chocolate/v1.png,/assets/products/chocolate/v2.png,/assets/products/chocolate/v3.png', 'Carefully crafted, this decadent Chocolate candle will fill your home with sweet and nostalgic scents of childhood. As if you are being taken back to a time of candy delights, this candle is sure to spark sweet memories.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (7, 'Lemon', 'Vulnerable Candle', 15.99, 8, '/assets/products/lemon/v1.png,vulnerablecandle/frontend/src/assets/products/lemon/v2.png,vulnerablecandle/frontend/src/assets/products/lemon/v3.png', 'Tangerine dreams come true with our invigorating tangy lemon candle! Let this summery scent transport you to a dream world of refreshing and enjoy the sweet, citrus aroma. Light the wick and let the pleasant, aromatic notes of lemon create a cheerful atmosphere.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (8, 'Coffee', 'Vulnerable Candle', 7.99, 41, '/assets/products/coffee/v1.png,vulnerablecandle/frontend/src/assets/products/coffee/v2.png,vulnerablecandle/frontend/src/assets/products/coffee/v3.png', 'Is the rushed morning making you drag? Our coffee candle is the perfect pick-me-up. Filled with the aroma of homemade brewed coffee, you will be woken up with a fresh and energizing scent. Kickstart your day in style with our candle shop\'s coffee candle!');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (9, 'Cinnamon', 'Vulnerable Candle', 7.99, 41, '/assets/products/cinnamon/v1.png,vulnerablecandle/frontend/src/assets/products/cinnamon/v2.png,vulnerablecandle/frontend/src/assets/products/cinnamon/v3.png', 'Take a Trip Down Memory Lane: With this cinnamon candle, your home will be filled with the inviting aroma of freshly baked cookies. Enjoy the comforting scents of a warm oven and the cardamom-like sweetness of freshly-ground cinnamon. With this candle, you can take a journey down memory lane, back to when baking was part of your weekly routine.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (10, 'Cedar', 'Vulnerable Candle', 19.99, 32, '/assets/products/cedar/v1.png,vulnerablecandle/frontend/src/assets/products/cedar/v2.png,vulnerablecandle/frontend/src/assets/products/cedar/v3.png', 'Take the inviting, outdoorsy aroma of fir and cedar trees into your home! Whether for a calming meditation or a relaxing evening, light this cedar candle and bask in the senses of nature! Feel connected to the peaceful outdoors as the scent of cedar and fir envelops your indoor spaces.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (11, 'Leather', 'Vulnerable Candle', 14.99, 23, '/assets/products/leather/v1.png,vulnerablecandle/frontend/src/assets/products/leather/v2.png,vulnerablecandle/frontend/src/assets/products/leather/v3.png', 'Enjoy the luxurious leather aromas of the Wild West with our leather-scented candle. Reminiscent of the South, this leathery scent is great for creating a warm and welcoming atmosphere. Yee haw, cowboys!');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (12, 'Incense', 'Vulnerable Candle', 6.99, 18, '/assets/products/insence/v1.png,/assets/products/incense/v2.png,vulnerablecandle/frontend/src/assets/products/incense/v3.png', 'Connect with your inner peace with our incense candle. Whether you’re in the midst of your meditation practice or just wanting a soothing atmosphere – the subtle scent of our candle brings a calming energy and invites you to indulge in your well-deserved zen moment.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (13, 'Peppermint', 'Vulnerable Candle', 15.99, 8, '/assets/products/peppermint/v1.png,vulnerablecandle/frontend/src/assets/products/peppermint/v2.png,vulnerablecandle/frontend/src/assets/products/peppermint/v3.png', 'Get into the winter spirit this season with our Peppermint Candle! This delightful scent is sure to bring joy to your home and make the holiday vibes complete, no matter the month or season. Add a mild peppermint fragrance to your décor and enjoy the spirit of the festive season whenever!');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (14, 'Pumpkin Spice', 'Vulnerable Candle', 24.99, 120, '/assets/products/pumpkin_spice/v1.png,vulnerablecandle/frontend/src/assets/products/pumpkin_spice/v2.png,vulnerablecandle/frontend/src/assets/products/pumpkin_spice/v3.png', 'Fall calls for warmth, comfort and the unmistakable scent of pumpkin spice! Our seasonal candle is here to fulfill your needs. Invite a festive atmosphere into your home with this pumpkin spiced candle that’ll finish off the perfect autumn look for your living space.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (15, 'Melon', 'Vulnerable Candle', 11.99, 33, '/assets/products/melon/v1.png,vulnerablecandle/frontend/src/assets/products/melon/v2.png,vulnerablecandle/frontend/src/assets/products/melon/v3.png', 'Let the calming, summery scent of our vegan plant-based melon candle whisk you away to memories of a relaxing vacation. It\'s an essential part of your at-home spa experience. Escape the everyday worries and relax with the perfect scent of summer.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (16, 'Strawberry', 'Vulnerable Candle', 17.99, 62, '/assets/products/strawberry/v1.png,vulnerablecandle/frontend/src/assets/products/strawberry/v2.png,vulnerablecandle/frontend/src/assets/products/strawberry/v3.png', 'Celebrate the arrival of Spring with our delectable, artisanal strawberry candle. A truly unique combination of homemade ingredients, this candle offers a stunningly natural aroma of freshly-picked, juicy strawberries. Cozy up and indulge in the sweet, homemade charm it brings to every room!');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (17, 'Mint', 'Vulnerable Candle', 13.99, 15, '/assets/products/mint/v1.png,vulnerablecandle/frontend/src/assets/products/mint/v2.png,vulnerablecandle/frontend/src/assets/products/mint/v3.png', 'Our all-natural mint candle is designed to soothe the senses and bring calming relaxation to all your worries. Crafted with a combination of refreshing peppermint and invigorating spearmint, the candle\'s light aroma will engulf you in a sense of peace and serenity.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (18, 'Tulip', 'Vulnerable Candle', 8.99, 39, '/assets/products/tulip/v1.png,vulnerablecandle/frontend/src/assets/products/tulip/v2.png,vulnerablecandle/frontend/src/assets/products/tulip/v3.png', 'Transform any room into a serene oasis with our tulip candles. Crafted with real flower petals and pure vegan wax, this candle gives off a delicate, calming scent of the outdoors when lit. Enjoy the healing effects of nature right inside your own home!');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (19, 'Blueberry', 'Vulnerable Candle', 5.99, 112, '/assets/products/blueberry/v1.png,vulnerablecandle/frontend/src/assets/products/blueberry/v2.png,vulnerablecandle/frontend/src/assets/products/blueberry/v3.png', 'Enhance your relaxation any time with this blueberry-scented candle. Crafted with natural materials, its refreshing scent will fill the room with a balance of sweet and calming vibes. Plant-based and hand-poured for a perfect blend of soothing aromas.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (20, 'Buttercream', 'Vulnerable Candle', 2.99, 40, '/assets/products/buttercream/v1.png,vulnerablecandle/frontend/src/assets/products/buttercream/v2.png,vulnerablecandle/frontend/src/assets/products/buttercream/v3.png', 'Indulge your senses with the irresistible aroma of freshly made buttercream, all wrapped up in a beautiful and elegant candle. The creamy and sweet fragrance will transport you to a place of pure bliss and relaxation. Light up this candle and let the warm glow and mouth-watering aroma envelop your space, creating an ambiance of comfort and coziness. Whether you\'re relaxing after a long day or entertaining guests, the Buttercream scented candle is the perfect addition to any setting.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (21, 'Allergies', 'Vulnerable Candle', 24.99, 55, '/assets/products/allergies/v1.png,vulnerablecandle/frontend/src/assets/products/allergies/v2.png,vulnerablecandle/frontend/src/assets/products/allergies/v3.png', 'Do you love the smell of the spring season, but wish it could be something even more authentic? Try our new rose-scented candle with allergies included! Invite pollen and airborne irritants into your spaces any time of the year! Sneeze and gasp for air with our homemade allergies candle!');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (22, 'Salt', 'Vulnerable Candle', 3.99, 205, '/assets/products/salt/v1.png,vulnerablecandle/frontend/src/assets/products/salt/v2.png,vulnerablecandle/frontend/src/assets/products/salt/v3.png', 'Add a special touch to your home décor with these salt candles. Crafted from naturally harvested pieces of Himalayan salt, each candle creates a tranquil ambience, bringing positive energy and serenity to the air.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (23, 'Fart', 'Vulnerable Candle', 8.99, 31, '/assets/products/fart/v1.png,vulnerablecandle/frontend/src/assets/products/fart/v2.png,vulnerablecandle/frontend/src/assets/products/fart/v3.png', 'If you\'re looking to turn an environment from fun to dreadful in a matter of moments, our fart candles are guaranteed to provide an awful, overpowering stench that will linger far past your desired time. Our fart candles provide the most awful, overpowering scent that will linger far beyond your expectations.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (24, 'Motel Room', 'Vulnerable Candle', 11.99, 235, '/assets/products/motel_room/v1.png,vulnerablecandle/frontend/src/assets/products/motel_room/v2.png,vulnerablecandle/frontend/src/assets/products/motel_room/v3.png', 'Do you love going out on a road trip, and wish you could be reminded of the memories of staying at cheap unclean motels! Now you can bring that experience home with you! Made with authentic used cigarettes and cheap cleaning products, you can bring the experience of travel anywhere you go!');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (25, 'New Car', 'Vulnerable Candle', 18.99, 15, '/assets/products/new_car/v1.png,vulnerablecandle/frontend/src/assets/products/new_car/v2.png,vulnerablecandle/frontend/src/assets/products/new_car/v3.png', 'There’s nothing quite like the smell of an unused car fresh out of the dealership. If you want that experience to last, use our patented new car candle! It won’t keep your car any cleaner, but it’ll smell brand new*! *does not work inside cars.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (26, 'New Car', 'Vulnerable Candle', 17.99, 37, '/assets/products/used_socks/v1.png,vulnerablecandle/frontend/src/assets/products/used_socks/v2.png,vulnerablecandle/frontend/src/assets/products/used_socks/v3.png', 'Bring the fun of smelly socks to your home with this unique candle! Relive your childhood days with the nostalgic scent of smelly socks, crafted with a contemporary and wax design for a warm and cozy atmosphere. With up to 35 hours of use, you can enjoy the nostalgic scent of smelly socks for a long time!');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (27, '2-Day Expired Chicken', 'Vulnerable Candle', 3.99, 85, '/assets/products/expired_chicken/v1.png,vulnerablecandle/frontend/src/assets/products/expired_chicken/v2.png,vulnerablecandle/frontend/src/assets/products/expired_chicken/v3.png', 'Who doesn\'t love the smell of an expired chicken? Now you can smell it anytime with our irresistibly cute chicken-shaped candle. It\'s crafted with a mix of herbs, spices and adventurous expired chicken that create an unforgettable scent. Place it anywhere to add a truly unique aroma.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (28, 'Onion', 'Vulnerable Candle', 14.99, 33, '/assets/products/onion/v1.png,vulnerablecandle/frontend/src/assets/products/onion/v2.png,vulnerablecandle/frontend/src/assets/products/onion/v3.png', 'Introducing our newest creation - the Onion scented candle! Experience the savory and pungent aroma of freshly cut onions in the comfort of your own home. This candle is perfect for any onion lover or anyone looking to add a unique twist to their candle collection. The Onion scented candle is made with high-quality fragrance oils that capture the distinct scent of onions. The candle\'s warm glow and unmistakable fragrance will create an ambiance of coziness and comfort in any room. Tears are included! Whether you\'re cooking in the kitchen or relaxing in your living room, our Onion scented candle is sure to delight your senses. So why not add this unique and bold scent to your collection today?');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (29, 'Garbage', 'Vulnerable Candle', 18.99, 94, '/assets/products/garbage/v1.png,vulnerablecandle/frontend/src/assets/products/garbage/v2.png,vulnerablecandle/frontend/src/assets/products/garbage/v3.png', 'Our garbage smelling candle is an ideal option if you\'re looking to vomit. It\'s formulated with an evocative aroma reminiscent of a garbage-strewn alley, so you can convince others that your home or space smells disgusting and vile. And it\'s easy to use - just light the candle, and allow its compelling scent to eliminate any friendship!');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (30, 'Swamp', 'Vulnerable Candle', 16.99, 67, '/assets/products/swamp/v1.png,vulnerablecandle/frontend/src/assets/products/swamp/v2.png,vulnerablecandle/frontend/src/assets/products/swamp/v3.png', 'Delve into the depths of the swamp with our unique scented candles! Get lost in the earthy and musky aromas and create an atmosphere that is both enchanting and calming. Whether you use it in your livingroom or your bathroom, the scent of the swamp will grace your home and give it an ethereal edge.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (31, 'Fire', 'Vulnerable Candle', 14.99, 2, '/assets/products/fire/v1.png,vulnerablecandle/frontend/src/assets/products/fire/v2.png,vulnerablecandle/frontend/src/assets/products/fire/v3.png', 'Love the smell of burning wood and charcoal? Want to make that experience permanent? With our new fire candle with a full gallon of unleaded gasoline, you can easily recreate that smell anywhere you go!');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (32, 'Plastic', 'Vulnerable Candle', 8.99, 51, '/assets/products/plastic/v1.png,vulnerablecandle/frontend/src/assets/products/plastic/v2.png,vulnerablecandle/frontend/src/assets/products/plastic/v3.png', 'Bring the unforgettable scent of burnt plastic wherever you go with our special scented candle. Whether you want to recreate memories of fond childhood days or simply enjoy its unique aroma, this candle is the perfect accompaniment for any home. Its smoky and earthy tones are sure to evoke a sense of nostalgia and make your living space seem that much more inviting.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (33, 'Farm', 'Vulnerable Candle', 7.99, 55, '/assets/products/farm/v1.png,vulnerablecandle/frontend/src/assets/products/farm/v2.png,vulnerablecandle/frontend/src/assets/products/farm/v3.png', 'This farm animal smelling candle is a special way to capture the essence of the countryside. Let the sweet and savory blend of barnyard animal aromas take you back to your favorite childhood farm as you relax in the soft glow of the candle’s flame. With notes of hay, wood, grass, manure, flies, and earth, this candle will truly give you a sense of nostalgia and tranquility.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (34, 'Wet Dog', 'Vulnerable Candle', 17.99, 88, '/assets/products/wet_dog/v1.png,vulnerablecandle/frontend/src/assets/products/wet_dog/v2.png,vulnerablecandle/frontend/src/assets/products/wet_dog/v3.png', 'Missing the smell of a damp dog in your home? Check out this awesome wet dog smelling candle! Its natural scents of wet dog odors quickly transform your house from sweet-smelling to smelly in seconds!. Not only that, but your pup will probably appreciate the calming scent too! Get the comfortable and welcoming home you desire and make your pup happy – get your wet dog smelling candle today.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (35, 'Mold', 'Vulnerable Candle', 18.99, 93, '/assets/products/mold/v1.png,vulnerablecandle/frontend/src/assets/products/mold/v2.png,vulnerablecandle/frontend/src/assets/products/mold/v3.png', 'With our all new mold-scented candle, the smell of month old unwashed dishes and stagnant water is at your fingertips! Made with all natural ingredients, feel your home wash into a brimming oasis of life! Just watch out for spores!');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (36, 'Burnt Popcorn', 'Vulnerable Candle', 6.99, 16, '/assets/products/burnt_popcorn/v1.png,vulnerablecandle/frontend/src/assets/products/burnt_popcorn/v2.png,vulnerablecandle/frontend/src/assets/products/burnt_popcorn/v3.png', 'Take a break from stress with our Burnt Popcorn Candle. You\'ll be reminded of a warm moment spent around a movie theater, with the aroma of savory burnt popcorn reminding you of simpler times. Light this cozy and comforting candle to breathe a sense of calm into your home.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (37, 'CO2', 'Vulnerable Candle', 2.99, 20, '/assets/products/co2/v1.png,vulnerablecandle/frontend/src/assets/products/co2/v2.png,vulnerablecandle/frontend/src/assets/products/co2/v3.png', 'Add atmosphere to your space with this unique carbon dioxide candle! With a refreshingly clean and crisp scent, it will fill your home with a calming and cozy atmosphere. Plus, with its 100% toxic and no-biodegradable wick, you can feel good about lighting up your home.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (38, 'Void', 'Vulnerable Candle', 13.99, 55, '/assets/products/void/v1.png,vulnerablecandle/frontend/src/assets/products/void/v2.png,vulnerablecandle/frontend/src/assets/products/void/v3.png', 'Find connection, peace, and harmony when you light our Void Candle. Feel its warm glow envelop the room, igniting a sense of clarity and purpose. Our unique fragrance provides an ideal relaxing atmosphere, where spiritual energy can cultivate, giving rise to profound insight. Remove the sense of smell from your life entirely!');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (39, 'Uranium', 'Vulnerable Candle', 14.99, 85, '/assets/products/uranium/v1.png,vulnerablecandle/frontend/src/assets/products/uranium/v2.png,vulnerablecandle/frontend/src/assets/products/uranium/v3.png', 'Elevate your living space with our exotic Uranium Candle. This eye-catching piece of decor will add a beautiful and captivating ambience to any room, providing a consistent green glow even when unlit. Feel secure with the knowledge that every aspect of this candle has been crafted with materials that are 100% toxic and harmful. Rejuvenate any living area with a delightful, serene atmosphere.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (40, 'Pizza', 'Vulnerable Candle', 17.99, 18, '/assets/products/pizza/v1.png,vulnerablecandle/frontend/src/assets/products/pizza/v2.png,vulnerablecandle/frontend/src/assets/products/pizza/v3.png', 'Infuse your space with the inviting scent of a freshly-baked pizza with this creative pizza-scented candle. Delight in the tantalizing aroma of the classic Italian dish and let it set the atmosphere for any cozy gathering. Add this unique candle to your home and be sure to heat up the atmosphere!');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (41, 'Random', 'Vulnerable Candle', 9.99, 500, '/assets/products/random/v1.png,vulnerablecandle/frontend/src/assets/products/random/v2.png,vulnerablecandle/frontend/src/assets/products/random/v3.png', 'Spend a consistent price to receive a random candle from our collection! Who knows what you’ll get? Enjoy!');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (42, 'Gym', 'Vulnerable Candle', 8.99, 91, '/assets/products/gym/v1.png,vulnerablecandle/frontend/src/assets/products/gym/v2.png,vulnerablecandle/frontend/src/assets/products/gym/v3.png', 'Get in shape with our gym candle! Illuminate your workout zone with a pleasurable fragrance. Our long burning candle will give you the comfort and relaxation you need for those tough gym sessions. Breathe the smell of sweat in deeply and let those soothing aromas make your gym routine even more rewarding.');
INSERT INTO ecom_product (id, name, brand, price, stock, image, description) VALUES (43, 'Salad', 'Vulnerable Candle', 12.99, 100, '/assets/products/salad/v1.png,vulnerablecandle/frontend/src/assets/products/salad/v2.png,vulnerablecandle/frontend/src/assets/products/salad/v3.png', 'The Salad Candle brings a unique and exquisite scent to your environment. Its natural aroma will bring a relaxing atmosphere to the entire room, while its light provides a calming and tranquil presence. Enjoy the aromatherapy and calming glow of the candle and enjoy peace in your home.');
